import React, { Component } from "react";
import { DropdownButton, Dropdown } from "react-bootstrap";
import List from "./List";

class FilteredList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      shopping: [],
      search: "",
      type: "All",
      size: "All",
      sort: "All",
      items: this.props.items,
      total:0

    };
  }


  typeFilter = event => {

    this.setState({ type: event});
  };

  sizeFilter = event => {

    this.setState({ size: event});
  };
  onSort = event => {

    this.setState({ sort: event});
  };

  isFilterType = item => {

    if(this.state.type === "All"){
      return true;
    }
    else if(this.state.type === item.type){
      return true;
    }
    else{
      return false;
    }
  }
  isFilterSize = item => {
    if(this.state.size === "All"){
      return true;
    }
    else if(this.state.size === item.size){
      return true;
    }
    else{
      return false;
    }
  }
  sortOptions = (item1, item2) => {
      if (this.state.sort === "All") {
        return 0;
      }
      else if (this.state.sort === "rating"){
        return item1.rating - item2.rating;
      }
    }

  filter = item => {
    return this.isFilterType(item) && this.isFilterSize(item);
  }

onAggregatorAdd = item=>{
  const array = [...this.state.shopping, item]
  this.setState({shopping: array, total: this.state.total+item.price});
};

onAggregatorRemove = item => {
  const removeItem = this.state.shopping.slice();
  removeItem.splice(item);
  this.setState({shopping: removeItem})
  this.setState({shopping: removeItem, total: this.state.total-item.price});
};
  render() {
    return (
      <div className="filter-list">
        <div className = "buttons">
        <DropdownButton title="Type" id="dropdownButton">
          <Dropdown.Item eventKey="All" onSelect={this.typeFilter}>
            All
          </Dropdown.Item>
          <Dropdown.Item eventKey="berry" onSelect={this.typeFilter}>
            Berry
          </Dropdown.Item>
          <Dropdown.Item eventKey="drupe" onSelect={this.typeFilter}>
            Drupe
          </Dropdown.Item>
          <Dropdown.Item eventKey="citrus" onSelect={this.typeFilter}>
            Citrus
          </Dropdown.Item>
          <Dropdown.Item eventKey="pome" onSelect={this.typeFilter}>
            Pome
          </Dropdown.Item>
        </DropdownButton>

        <DropdownButton title="Size" id="dropdownButton">
          <Dropdown.Item eventKey="All" onSelect={this.sizeFilter}>
              All
          </Dropdown.Item>
          <Dropdown.Item eventKey="Small" onSelect={this.sizeFilter}>
            Small
          </Dropdown.Item>
          <Dropdown.Item eventKey="Medium" onSelect={this.sizeFilter}>
            Medium
          </Dropdown.Item>
          <Dropdown.Item eventKey="Large" onSelect={this.sizeFilter}>
            Large
          </Dropdown.Item>
        </DropdownButton>

        <DropdownButton title="Sort" id="dropdownButton">
          <Dropdown.Item eventKey="All" onSelect={this.onSort}>
            All
          </Dropdown.Item>
          <Dropdown.Item eventKey="Rating" onSelect={this.onSort}>
            Rating
          </Dropdown.Item>


         </DropdownButton>
      </div>

        <List items={this.props.items.filter(this.filter).sort(this.sortOptions)} onAggregatorAdd={this.onAggregatorAdd} onAggregatorRemove={this.onAggregatorRemove}/>
<div class="add">
<h4 id="title"> Cart</h4>
<h4 id="text"> Total = ${Math.abs(this.state.total.toFixed())}</h4>
<List items= {this.state.shopping} onAggregatorRemove={this.onAggregatorRemove} onAggregatorAdd={this.onAggregatorAdd} />
  </div>
  </div>

    );
  }
}

export default FilteredList;
