import React, { Component } from "react";
import { DropdownButton, Dropdown } from "react-bootstrap";
import List from "./List";

class FilteredList extends Component {
  constructor(props) {
    super(props);

    //The state is just a list of key/value pairs (like a hashmap)
    //TODO (FilteredList): Add an additional state variable within this.state called "type" and set it to a default value
    this.state = {
      search: "",
      type: "All",
      company: "All",
      sort: "All",

    };
  }



  /*
   * This function gets called every time a new filter type is selected in the dropdown. Your job is to handle the state
   * changes that should occur when a new filter type is selected.
   */

  onSelectFilterType = event => {
    //TODO Set the state of the filter type in this.state
    this.setState({ type: event});
  };

  onSelectFilterSize = event => {
    //TODO Set the state of the filter type in this.state
    this.setState({ size: event});
  };
  onSort = event => {
    //TODO Set the state of the filter type in this.state
    this.setState({ sort: event});
  };
  //onFavorite = event => {
    //TODO Set the state of the filter type in this.state
  //  this.setState({ favorited: event});
//  } ;

  /*
   * This function should determine whether the item being passed in matches the type
   * that we are filtering on. Remember that the selected type we are filtering on is stored
   * in this.state!
   * Input: An element from your List component
   * Output: true or false
   */
  matchesFilterType = item => {
    // TODO: add conditions to check if item type is equal to selected type
    if(this.state.type === "All"){
      return true;
    }
    else if(this.state.type === item.type){
      return true;
    }
    else{
      return false;
    }
  }
  matchesFilterSize = item => {
    // TODO: add conditions to check if item type is equal to selected type
    if(this.state.size === "All"){
      return true;
    }
    else if(this.state.size === item.size){
      return true;
    }
    else{
      return false;
    }
  }
  sortOptions = (item1, item2) => {
      if (this.state.sort === "All") {
        return 0;
      }
      else if (this.state.sort === "Popularity"){
        return item1.popularity - item2.popularity;
      }
    }

  /*
   * The function is passed into a builtin filter() function. filter() calls this function on every element
   * in the list. If this fucntion returns true for a given element, filter() will add that element to its
   * return list.
   */
  filterAndSearch = item => {
    return item.name.toLowerCase().search(this.state.search) !== -1 && this.matchesFilterType(item) && this.matchesFilterSize(item);
  }

  render() {
    return (
      <div className="filter-list">

        {/* TODO: Add more menu items with onSelect handlers*/}
        <div className = "buttons">
        <DropdownButton title="Type" id="dropdown-basic-button">
          <Dropdown.Item eventKey="All" onSelect={this.onSelectFilterType}>
            All
          </Dropdown.Item>
          <Dropdown.Item eventKey="Desktop" onSelect={this.onSelectFilterType}>
            Desktop
          </Dropdown.Item>
          <Dropdown.Item eventKey="Laptop" onSelect={this.onSelectFilterType}>
            Laptop
          </Dropdown.Item>
          <Dropdown.Item eventKey="Tablet" onSelect={this.onSelectFilterType}>
            Tablet
          </Dropdown.Item>
          <Dropdown.Item eventKey="Phone" onSelect={this.onSelectFilterType}>
            Phone
          </Dropdown.Item>
        </DropdownButton>

        <DropdownButton title="Brand" id="dropdown-basic-button">
          <Dropdown.Item eventKey="All" onSelect={this.onSelectFilterCompany}>
            All
          </Dropdown.Item>
          <Dropdown.Item eventKey="Apple" onSelect={this.onSelectFilterCompany}>
            Apple
          </Dropdown.Item>
          <Dropdown.Item eventKey="Microsoft" onSelect={this.onSelectFilterCompany}>
            Microsoft
          </Dropdown.Item>
          <Dropdown.Item eventKey="Samsung" onSelect={this.onSelectFilterCompany}>
            Samsung
          </Dropdown.Item>
          <Dropdown.Item eventKey="Dell" onSelect={this.onSelectFilterCompany}>
            Dell
          </Dropdown.Item>
        </DropdownButton>

        <DropdownButton title="Sort" id="dropdown-basic-button">
          <Dropdown.Item eventKey="All" onSelect={this.onSort}>
            All
          </Dropdown.Item>
          <Dropdown.Item eventKey="Popularity" onSelect={this.onSort}>
            Popularity
          </Dropdown.Item>


         </DropdownButton>
      </div>
      </div>

    );
  }
}

export default FilteredList;
